USE MegaHerdtAPI
DECLARE @id as int;

SET @id = (SELECT ISNULL(MAX(Id),0) FROM ArticlesBrands);
DBCC CHECKIDENT(ArticlesBrands, RESEED, @id)
SET IDENTITY_INSERT [dbo].[ArticlesBrands] ON
SET IDENTITY_INSERT [dbo].[ArticlesBrands] OFF

